import { AddRounded, RemoveRounded } from '@mui/icons-material';
import React, { useEffect, useState } from 'react';
import { useStateValue } from './StateProvider';

let cartItems = [];

const CartItem = ({ name, imgSrc, price, itemId }) => {
  const [qty, setQty] =useState(1);
  const [itemPrice, setItemPrice] = useState(parseInt(qty) * parseFloat(price))
  const [{cart}, dispatch] = useStateValue();

  useEffect(()=>{
    cartItems = cart;
  },[])

  const updateQuantity = (action, imgSrc) => {
    if(action === 'add') {
      setQty(qty + 1);
    }
    else if(action === 'remove') {
      setQty(qty - 1);
    }
  };

  return (
    <div className='cardItem'>
      <div className='imgBox'>
        <img src={imgSrc} alt='item' />
      </div>

      <div className='itemSection'>
        <h2 className='itemName'>{name}</h2>
        <div className='itemQuantity'>
          <span>x {qty}</span>
          <div className='quantity'>
            <RemoveRounded className='itemRemove' onClick={() => updateQuantity('remove', imgSrc) }/>

            <AddRounded className='itemAdd' onClick={() => updateQuantity('add', imgSrc)} />
          </div>
        </div>
      </div>

      <p className='itemPrice'>
        <span className='dolorSign'>$ </span>
        <span className='itemPriceValue'>{price}</span>
      </p>
    </div>
  );
};

export default CartItem;
