import { AccountBalance, Chat, Favorite, HomeRounded, Settings, SummarizeRounded } from '@mui/icons-material';
import './App.css';
import Header from './Components/Header'
import MenuContainer from './Components/MenuContainer';

function App() {

  useEffect(() => {
    const menuLi = document.querySelectorAll('#menu li');

    const setMenuActive = () => {
      menuLi.forEach(li => li.classList.remove('active'));
      this.classList.add('active');
    };

    menuLi.forEach(item => item.addEventListener('click', setMenuActive));
  }, []);

  return (
    <div className="App">

     {/* Header Section */}
      <Header />

     {/* Main Container */}
      <main>
        <div classNAme='mainContainer'></div>
        <div className='rightMenu'></div>
      </main>

     {/* Bottom Menu */}

     <div className='bottomMenu'>
       <ul id='menu'>
         <MenuContainer link={'#'} icon={<HomeRounded />} />
         <MenuContainer link={'#'} icon={<Chat />} />
         <MenuContainer link={'#'} icon={<AccountBalance />} />
         <MenuContainer link={'#'} icon={<Favorite />} />
         <MenuContainer link={'#'} icon={<SummarizeRounded />} />
         <MenuContainer link={'#'} icon={<Settings />} />

         <div className='indicator'></div>
       </ul>
     </div>
    </div>
  );
}

export default App;
